import React, { useState } from 'react'
import howToUseApp from "../API/howToUse";

const Aboutus = () => {
  /*  use state hooks  */
  const [aboutData, setAboutData] = useState(howToUseApp);
  return (
    <>
        <section className="common-section our-services">
          <div className="container mb-5">
            <div className="row">
              <div className="col-12 col-lg-5 text-center our-service-leftside-img">
                <img src="./images/he.jpg" alt="aboutusImg" />

              </div>

              {/* 1section right side data */}

              <div className="col-12 col-lg-7 our-services-list">
                <h3 className='mini-title'>
                  --AVAILABLE @ GOOGLE AND IOS APP STORE ONLY
                </h3>
                <h1 className="main-heading">How to use the App?</h1>

                {aboutData.map((curElem) => {
                  const { id, title, info } = curElem;
                  return(
                    <>
                    <div className="row our-services-info" key={id}>
                      <div className="col-1 our-services-number">{id}</div>
                      <div className="col-10 our-services-data">
                        <h2>{title}</h2>
                        <p className="main-hero-para">
                        {info}
                        </p>
                      </div>
                    </div>
                    </>
                  );
                })}
               
                <br />
                <button className="btn-style btn-style-border">Learn more</button>
              </div>

            </div>

          </div>

        </section>

        {/* 2nd section about us */}
        <section className="common-section our-services">
          <div className="container mb-5">
            <div className="row">

              {/* 2section left side data */}

              <div className="col-12 col-lg-7 our-services-list">
                <h3 className='mini-title'>
                  --SUPPOR IN ANY LANGUAGES
                </h3>
                <h1 className="main-heading">World class support is<br /> available 24/7</h1>

                {aboutData.map((curElem) => {
                  const { id, title, info } = curElem;
                  return(
                    <>
                    <div className="row our-services-info" key={id}>
                      <div className="col-1 our-services-number">{id}</div>
                      <div className="col-10 our-services-data">
                        <h2>{title}</h2>
                        <p className="main-hero-para">
                        {info}
                        </p>
                      </div>
                    </div>
                    </>
                  );
                })}
               
                <br />
                <button className="btn-style btn-style-border">Learn more</button>
              </div>

              <div className="col-12 col-lg-5 text-center our-service-leftside-img">
                <img src="./images/" alt="aboutusImg" />

              </div>

            </div>

          </div>

        </section>
    </>
  );
};

export default Aboutus
